const server = require('./test/fixtures/server');
const tls = require('tls');
const _cache = tls.createSecureContext;

tls.createSecureContext = function () {
    console.log(arguments)
    return _cache.apply(this, arguments);
};


sslServer = server.createSSLServer({
    ciphers: 'ECDHE-RSA-AES256-GCM-SHA384:ECDHE-RSA-AES128-GCM-SHA256'
});
sslServer.on('/', function (req, res) {
    res.writeHead(200, {
        'Content-Type': 'text/plain'
    });
    res.end('okay');
});
sslServer.listen(9090, () => {
    console.log(sslServer.url)
});
